const items = $input.first().json.documentos;

const seen = new Set();
const documents = [];

items.forEach(item => {
  const text = item.document.pageContent;
  if (text && !seen.has(text)) {
    seen.add(text);
    documents.push(text);
  }
});

return [{
  json: {
    documents
  }
}];

const items = $input.all();

// Objeto para trackear los pageContent únicos
const uniqueContents = {};
const uniqueItems = [];

items.forEach((item, index) => {
  const content = item.json.document.pageContent;
  if (!uniqueContents[content]) {
    uniqueContents[content] = true;
    uniqueItems.push(item.json);
  }
});

// Ordenar por score descendente
uniqueItems.sort((a, b) => b.score - a.score);

// Asignar ID según la posición ordenada
const documentosConId = uniqueItems.map((item, index) => ({
  id: index, // ID basado en la posición final
  document: item.document,
  score: item.score
}));

return [{ json: { documentos: documentosConId } }];

// Habilitar "Run Once for All Items"
const results = items[0].json.results;
console.log(results);

// Primero filtrar los que tengan score mayor a 0.8
const filteredResults = results.filter(item => item.score > 0.07);

// Luego ordenar por score de mayor a menor (descendente)
const sortedResults = filteredResults.sort((a, b) => b.score - a.score);

return [{
  json: {
    results: sortedResults
  }
}];

const results = $input.first().json.results;
const documentos = $('eliminar_duplicados').first().json.documentos;

for (let result of results) {
  for (let doc of documentos) {
    // Asegúrate de que pageContent esté en el nivel correcto
    const pageContent = doc.document?.pageContent;
    if (result.text === pageContent) {
      result.metadata = doc.document.metadata;
      break; // Ya lo encontraste, no necesitas seguir buscando
    }
  }
}

return [{ json: { results } }];

