function generarIdCorto() {
    return Date.now().toString(36) + Math.random().toString(36).substring(2);
}


const asistencias = $('EXTRACT_ATTENDANCE').all().map(as=>as.json)
const votos = $('GET_VOTE_DATA').all().map(vot=>vot.json)
//filtro los votos de las personas que no vinieron


//ANALIZO PARA VER SI EN LA REUNION NO SE TOPÓ ALGÚN TEMA, ESO SE DA CUANDO SE ELIMINA EL TEMA DE LA TABLA VOTACIONES. SE LO REALIZA ANTES DE AGREGAR LOS POSIBLES TEMAS NUEVOS. (CON LOS QUE SE DEFINIÓ EN LA CONVOCATORIA)
// 0. Obtener topics únicos de los votos
const uniqueTopicsVotes = [...new Set(votos.map(item => item.topic))];

// 1. Obtener la referencia al metadata original
const topicsMetadata = $('EXTRACT_METADATA').first().json.data[0].topics;

// 2. Crear un Set para búsqueda más eficiente
const votesSet = new Set(uniqueTopicsVotes);

// 3. Solo marcar los topics en el metadata
topicsMetadata.forEach(topicObj => {
    topicObj.disabled = !votesSet.has(topicObj.topic);
});




//Primero analizo los votos para verificar si hay nuevos temas
//si existen nuevos temas se crea la estructura para añadir a los votos que se extrayeron de la base
//para ello se usa la tabla votos
const votos_filtered = votos.filter(voto => 
  asistencias.some(asist => 
    asist?.name.trim().toLowerCase() === voto.name.trim().toLowerCase() && 
    asist?.attendance?.toLowerCase() === "x"
  )
);
// 1. Filtrar elementos sin 'id_topic'
const filtered = votos_filtered.filter(item => !item.hasOwnProperty('id_topic'));
// 2. Obtener topics únicos
const uniqueTopics = [...new Set(filtered.map(item => item.topic))];

// 3. Crear un Map para asociar cada topic con un ID único generado
const topicIdMap = new Map();

uniqueTopics.forEach(topic => {
    const id = generarIdCorto();
    topicIdMap.set(topic, id);
});


// 4. Crear el array result con IDs
const result = uniqueTopics.map(topic => ({
    id_topic: topicIdMap.get(topic),
    topic: topic,
    sub_topics: []
}));
// 5. Actualizar el array original votos agregando id_topic
votos_filtered.forEach(item => {
    if (!item.hasOwnProperty('id_topic') && topicIdMap.has(item.topic)) {
        item.id_topic = topicIdMap.get(item.topic);
    }
});
/**
 * /COLOCO LAS VOTACIONES DENTRO DE LOS TEMAS LA IDEA ES TENER ESTA ESTRUCTURA
temas_reunion: [
{
        "id": "101",
        "createdTime": "",
        "descripcion": "*Nombre del tema*",
        "subtemas": "*subtemas separados por saltos de linea*",
        "tabla": "temas_reunion",
        "meeting_summary": "str",
        votes: [
        {
            "id": "*id recordAirtable",
            "createdTime": "2025-09-10T13:24:44.000Z",
            "votante": "MSc. Dania Elena Portelles Cobas",
            "opcion": "AF",
            "tema": "Aprobación de la designacion de los miembros del comité de ética del instituto superior tecnológico yaruquí, quedando conformado de la siguiente manera",
            "tabla": "votos",
            "id_tema": "101",
            "cargoVotante": "Vicerrectora Académica Instituto Superior Tecnológico Yaruquí"
          },
        ]
 }
,...]

 */
const id_resolucion = (num_resolution)=>{
  /*
   PERMITE OBTENER EL CODIGO DE LA RESOLUCIÓN SEGUN EL INDEX DEL TEMA VISTO
   DADO QUE HAY VARIOS TEMAS DENTRO DE UNA REUNION, SE VA UTILIZANDO ESTA FUNCION DE ACUERDO AL INDICE DEL TEMA VISTO.
   */
  const metadata_resolutions = $('GET_NUMBER_SESSION').first().json
  const type_session = $('EXTRACT_METADATA').first().json.data[0].meeting_type
  //primero obtengo el numero de la resolucion
  const number_session = type_session.toLowerCase() == "ordinaria" ? 
    ('00' + (Number(metadata_resolutions.ordinary_session_number) + 1)
).slice(-3) : 
    ('00' + (Number(metadata_resolutions.extraordinary_session_number) + 1)
).slice(-3)
  const resolution_name =  `R-OCS-S${type_session.toLowerCase() == "ordinaria" ? "O" : "E"}-${number_session}-Nro.${num_resolution}-${$today.toString().substring(0, 4)}`
  return resolution_name
}
const countVotesFunction = (votos) => votos.reduce((acc, { option = "", name }) => {
  const key = option.toLowerCase().includes("favor") ? "AF" : option.toLowerCase().includes("contra")  ? "EC" : "AB";
  acc[key]++;
  if (/javier\W*garzon/i.test(name.normalize("NFD").replace(/[\u0300-\u036f]/g, ""))) acc.RECTOR_VOTE = key;
  return acc;
}, { AF: 0, EC: 0, AB: 0, RECTOR_VOTE: null });


//Extraigo las notificaciones unicas
const uniqueNotifications = votos.filter(item => 
  item.notification !== undefined && item.notification !== null
);

//antes de colocar los votos, agrego los temas nuevos en caso de que existan
const temasOriginales = $('EXTRACT_METADATA').first().json.data[0].topics;
const temas_reunion = [...temasOriginales, ...result];


temas_reunion.forEach((tema, index) => {
  const votes = votos_filtered
    .filter(voto => voto.id_topic == tema.id_topic)
    .map(voto => {

      // Primero, crear el objeto base (con opcion si falta)
      const votoBase = !voto.hasOwnProperty('option') 
        ? { ...voto, opcion: "Abstencion" }
        : voto;

      return {
        ...votoBase,
        role: $('EXTRACT_METADATA').first().json.data[0].attendees.find(integ => integ.name === voto.name)?.role || "NA"
      };
    });


 //Aniado norificaciones en caso de que existan
  const notificacion = uniqueNotifications.find(notif => notif.id_topic == tema.id_topic || notif.topic == tema.topic)?.notification || "";


  const resol_code = id_resolucion(index+1+Number($('GET_NUMBER_SESSION').first().json.resolution_number))
  tema.resolution_code = resol_code
  tema.vote_count_object = countVotesFunction(votes)
  tema.votes = votes;
  tema.approved = tema.vote_count_object.AF -   tema.vote_count_object.EC - tema.vote_count_object.AB > 0;
  tema.notification = notificacion

  tema.meeting_summary = "Lorem ipsum dolor sit amet consectetur adipiscing elit iaculis fringilla, urna fames ridiculus interdum fusce cubilia dictum hendrerit tempus magnis, euismod magna auctor imperdiet cursus pellentesque varius enim. Tristique lacus venenatis iaculis interdum lobortis semper libero, congue suspendisse pretium tempor scelerisque orci ultrices augue, convallis laoreet fringilla condimentum dui ut.\n Ullamcorper vel tempor conubia ut metus lacus, diam mauris penatibus risus dapibus fringilla mollis, justo vitae volutpat vehicula dis.Erat aptent orci phasellus viverra ligula, justo sodales tristique sem, arcu varius felis molestie. Mollis neque ultrices hac nulla pellentesque est diam phasellus suspendisse justo eget platea, risus hendrerit elementum condimentum vitae a eu tellus nullam vestibulum porttitor tristique feugiat, volutpat consequat curabitur lobortis purus mauris nibh tempor mi ullamcorper duis. Natoque phasellus quis varius vitae accumsan donec et sed sodales luctus imperdiet magna nostra aliquet, ornare porttitor class eget himenaeos dapibus ac mauris inceptos orci egestas habitasse.";
});

const newObj = $('EXTRACT_METADATA').first().json.data[0]
newObj.attendances = asistencias
newObj.topics = temas_reunion

const regex = /meeting[^%]*/;


//LINK DE REUNION
newObj.id_teams_meeting = $('EXTRACT_METADATA')?.first()?.json?.data?.[0]?.meeting_url?.match(regex)?.[0] || '';
//NUMERO DE SESSIN
newObj.number_sessions = $('GET_NUMBER_SESSION').first().json

return [{
  json: newObj
}];

// Esto devuelve 3 items (uno por cada topic)
// se calcula el nuevo numero contador para las resoluciones.

const number_sessions = $('PutIAdataInObject').first().json.number_sessions;

const resol_number = number_sessions.resolution_number + $('PutIAdataInObject').first().json.topics.length;

const estructure_data =  $('estructure_data').first().json
return $('PutIAdataInObject').first().json.topics.map(topic => ({
  json: {
    ...estructure_data,
    number_resolution_updated : resol_number,
    topics: undefined, // o simplemente omitirla
    topic: topic
  } 
}));

/*
EL NODO DE IA ANTERIOR CREA MENSAJES PERSONALIZADOS DE ACUERDO A SI SE ACEPTA,  SE NIEGA LA RESOLUCION, TAMBIEN PERMITE ELIMINAR CUALQUIER INTRODUCCION A ALGUN SUBPUNTO.
ESTA FUNCION PERMITE UNIR NUEVAMENTE ESA SALIDA A CADA TEMA VISTO (LISTA TOPICS)
*/

// Acceder al primer elemento del nodo 'estructure_data'
const structureData = $('estructure_data').first().json;

// Obtener el string JSON desde la entrada
const jsonString = $('ValidarTituloResolucion').first().json.candidates[0].content.parts[0].text
const jsonString_RESUMEN = $input.first().json.candidates[0].content.parts[0].text
// Parsear el string a un objeto JavaScript
const parsedData = JSON.parse(jsonString);
const parsedData_RESUMEN = JSON.parse(jsonString_RESUMEN);

// Dividir las cadenas en arrays usando " | " como separador
const reformulacionArray = parsedData.reformulacion.split("|");
const desitionArray = parsedData.desition.split("|");
const RESUMEN_ARRAY = parsedData_RESUMEN.resumenes.split("|");

// Verificar que la cantidad de topics coincida con los arrays
if (structureData.topics.length !== reformulacionArray.length || structureData.topics.length !== desitionArray.length) {
  throw new Error(`La cantidad de topics (${structureData.topics.length}) no coincide con la cantidad de elementos en reformulacion (${reformulacionArray.length}) o desition (${desitionArray.length}).`);
}

// Agregar las propiedades a cada topic
structureData.topics.forEach((topic, index) => {
  topic.reformulacion = reformulacionArray[index];
  topic.desition = desitionArray[index];
  topic.meeting_summary = RESUMEN_ARRAY[index]
});

// Devolver los datos modificados
return structureData;

