function generarIdCorto() {
    return Date.now().toString(36) + Math.random().toString(36).substring(2);
}

const allData = $input.all()
const votos =  allData[0].json.votes
const asistencias = allData[1].json.attendance
const metadata = allData[2].json.data[0]

/**
 * Determina si una resolución fue aprobada o negada según las reglas de votación.
 * 
 * Lógica de aprobación:
 * - Las resoluciones se adoptan con los votos de la mitad más uno de los miembros con derecho a voto asistentes
 * - El presidente (rector) tiene voto dirimente en caso de empate
 * - Las abstenciones (AB) se cuentan para el total de asistentes pero no influyen en la decisión
 * 
 * Criterio de aprobación:
 * - La resolución se aprueba si: VOTOS_AF > TOTAL_VOTANTES / 2
 * - En caso de empate (AF === EC), se aplica el voto dirimente del rector:
 *   • Si RECTOR_VOTE = "AF" → se suma 1 a AF
 *   • Si RECTOR_VOTE = "EC" → se suma 1 a EC  
 *   • Si RECTOR_VOTE = "AB" → no se modifica ningún voto
 * 
 * @param {Object} vote_count_object - Objeto que contiene el conteo de votos
 * @param {number} vote_count_object.AF - Cantidad de votos a favor
 * @param {number} vote_count_object.EC - Cantidad de votos en contra
 * @param {number} vote_count_object.AB - Cantidad de abstenciones
 * @param {string} vote_count_object.RECTOR_VOTE - Voto del rector ("AF", "EC" o "AB")
 * @returns {boolean} true si la resolución fue aprobada, false si fue rechazada
 * 
 * @example
 * // Caso 1: Aprobación sin empate
 * isResolutionApproved({AF: 3, EC: 1, AB: 1, RECTOR_VOTE: "EC"});
 * // Total = 5, AF > 5/2 (2.5) → 3 > 2.5 → true
 * 
 * @example  
 * // Caso 2: Empate con voto dirimente a favor
 * isResolutionApproved({AF: 2, EC: 2, AB: 1, RECTOR_VOTE: "AF"});
 * // Empate detectado → AF += 1 → AF:3, EC:2 → 3 > 5/2 (2.5) → true
 * 
 * @example
 * // Caso 3: Empate con voto dirimente en contra  
 * isResolutionApproved({AF: 2, EC: 2, AB: 1, RECTOR_VOTE: "EC"});
 * // Empate detectado → EC += 1 → AF:2, EC:3 → 2 > 2.5 → false
 * 
 * @example
 * // Caso 4: Empate con abstención del rector
 * isResolutionApproved({AF: 2, EC: 2, AB: 1, RECTOR_VOTE: "AB"});
 * // Empate detectado → sin cambios → 2 > 2.5 → false
 * 
 * @example
 * // Caso 5: Rechazo sin empate
 * isResolutionApproved({AF: 1, EC: 3, AB: 1, RECTOR_VOTE: "AF"});
 * // 1 > 5/2 (2.5) → false (no hay empate, no se aplica voto dirimente)
 */
function isResolutionApproved(vote_count_object) {

    let { AF, EC, AB, RECTOR_VOTE } = vote_count_object;
    const total = AF + EC + AB;

    // Si hay empate, aplicar voto dirimente del rector
    if (AF === EC) {
        switch (RECTOR_VOTE) {
            case "AF":
                AF += 1;
                break;
            case "EC":
                EC += 1;
                break;
            case "AB":
                // No se modifica ningún voto
                break;
        }
    }

    // La resolución se aprueba si AF es mayor que la mitad del total
    return AF > total / 2;
}


//filtro los votos de las personas que no vinieron


//ANALIZO PARA VER SI EN LA REUNION NO SE TOPÓ ALGÚN TEMA, ESO SE DA CUANDO SE ELIMINA EL TEMA DE LA TABLA VOTACIONES. SE LO REALIZA ANTES DE AGREGAR LOS POSIBLES TEMAS NUEVOS. (CON LOS QUE SE DEFINIÓ EN LA CONVOCATORIA)
// 0. Obtener una lista con los temas que están en la reunión (LOS QUE SE HICIERON VOTACIONES, DEL REGISTRO DE VOTOS)
const votesSet = new Set(votos.map(item => item.topic));

// 1. Obtengo los temas que se plantearon inicialmente (Cuando se generó la reunión)
const topicsMetadata = metadata.topics;
// 2. busco y marco domo desabilitado los temas que no se vieron en la reunión
topicsMetadata.forEach(topicObj => {
    //Si algun tema agendado (TEMA_EXTRAIDO_DE_METADATA), no se encuentra en el registro actual de reunión (TEMAS_FILTRADOS_DE_LOS_VOTOS_DE_LA_REUNION), se lo marca como desabilitado.
  //Esto solo analiza los temas que se agendaron, no en caso de que se haya creado un nuevo tema en la reunión.
    topicObj.disabled = !votesSet.has(topicObj.topic);
});



//Se eliminan los votos de los asistentes que no vinieron a la reunión
// filtro de asistentes con "x"
const votos_filtered = votos.filter(voto =>
  asistencias.some(asist =>
    String(asist?.name || '').trim().toLowerCase() === String(voto?.name || '').trim().toLowerCase() &&
    String(asist?.attendance || '').toLowerCase() === 'x'
  )
);
// Si es que hay, se obtiene los temas nuevos que se vieron en la reunión, los "TEMAS VARIOS"
const filtered = votos_filtered.filter(item =>
  // verdadero si NO hay propiedad id_topic o su valor es falsy (null, undefined, '')
  !Object.prototype.hasOwnProperty.call(item, 'id_topic') || !item.id_topic
);

//Me quedo únicamente con la descripcion de los temas nuevos
const uniqueTopics = Array.from(new Set(filtered.map(item => (item.topic || '').trim())));

//ASIGNO NUEVOS IDS a los "temas varios"
const topicIdMap = new Map();
uniqueTopics.forEach(topic => {
    const id = generarIdCorto();
    topicIdMap.set(topic, id);
});

const votos_con_id_asignado = votos_filtered.map(item => {
    // Si el item no tiene id_topic y su tema está en nuestro mapa de temas nuevos...
    if (!item.id_topic && topicIdMap.has(item.topic)) {
        // ...retornamos un NUEVO objeto, copiando todo lo del item original
        // y añadiendo la nueva propiedad id_topic.
        return {
            ...item,
            id_topic: topicIdMap.get(item.topic)
        };
    }
    // Si no necesita cambios, retornamos el item original sin tocarlo.
    return item;
});


// 4. Crear el array result con IDs
const result = uniqueTopics.map(topic => ({
    id_topic: topicIdMap.get(topic),
    topic: topic,
    sub_topics: []
}));

// 5. Actualizar el array original votos agregando id_topic
votos_filtered.forEach(item => {
    if (!item.hasOwnProperty('id_topic') && topicIdMap.has(item.topic)) {
        item.id_topic = topicIdMap.get(item.topic);
    }
});

