const attendeesHTML = $input.first().json.attendees
.map(attendee => {
  return `<p>${attendee.name}<br><strong>${attendee.role}</strong></p>`;
}).join('');


const topics_meeting = () =>{
    // Preprocesar los temas de la reunión
  //Se returna un array donde cada elemento es el tema de la reunión en html
  let topicsHTML = [];
  const newTopic = "Temas Varios.";


  // Agregar todos los temas existentes
  $input.first().json.meeting_topics.forEach((topic, index) => {
    let temp_topic = `<li>${topic.topic}</li>`;

    //extraigo los subtemas, deben estar separados por linea
    if (topic.sub_topics && topic.sub_topics.length > 0){
      temp_topic += '<ul>';
      topic.sub_topics.forEach(subtema => {
        temp_topic += `<li>${subtema}</li>`;
      });
      temp_topic += '</ul>';
    }

    topicsHTML.push(temp_topic)
  });

  // Validar si es reunión ordinaria (case insensitive)
  if ($input.first().json.meeting_type.toLowerCase().includes('ordinaria')) {
    topicsHTML.push(`<li>${newTopic}</li>`);
  }

  return topicsHTML

}


//se arma el html para un puntoc
const tema_reunion = (topic) =>{
  /***Construye el HTML correspondiente a un tema que hayan conversado en la reunión
  INPUT:
  description: Título del tema a tratar
  a_presentes: Lista de asistentes que votan
  votes: Lista de votos de asistentes
  meeting_summary: Resumen de maximo dos parrafos con lo que se conversó en el tema.
  ***/


  const text = `<p><strong>${topic.reformulacion}</strong></p><p>${topic.meeting_summary}</p><p>Concluida la intervenci&oacute;n, la MSc. Dania Elena Portelles Cobas propone la aprobaci&oacute;n del punto correspondiente del orden del d&iacute;a. La moci&oacute;n es secundada por la Lcda. Valeria Elizabeth Pinango Farinango, tras lo cual se procede a la votaci&oacute;n respectiva</p><div class="table_component" role="region" tabindex="0">${tabla_votacion(topic.votes)}</div><p>Con ${topic.vote_count_object["AF"]|| 0} a favor, ${topic.vote_count_object["EC"]|| 0} en contra, y ${topic.vote_count_object["AB"]|| 0} abstenciones, se resuelve:</p>`
  return text
}

const tabla_votacion = (votes) =>{
/***Construye el HTML correspondiente a un tema que hayan conversado en la reunión
  INPUT:
  votes: array de objetos
  ESTRUCTURA 
  [{name:"Javier", position: "Rector", vote:"F"}...]
  vote: F-> A FAVOR, C: EN CONTRA, A: ABSTENSION
  ***/
  let html_table = `<div class="table_component" role="region" tabindex="0"><table><thead><tr><th>Nombres y apellidos</th><th>Cargo</th><th>A favor</th><th>En contra</th><th>Abstenci&oacute;n</th></tr></thead><tbody>`
   votes.forEach((p) => {
    html_table += `<tr>
        <td>${p.name || 'NA'}</td>
        <td>${p.role}</td>
        <td>${p.option === 'A favor' ? 'X' : '&nbsp;'}</td>
        <td>${p.option === 'En contra' ? 'X' : '&nbsp;'}</td>
        <td>${(!p.option || p.option === 'Abstencion') ? 'X' : '&nbsp;'}</td>
    </tr>`;
});
  html_table += `</tbody></table></div>`
  return html_table
}


 //Se genera el HTML por cada tema tratado
const html_topics_covered = $input.first().json.meeting_topics.map(topic=>{
  return tema_reunion(topic)
})



const html_assistance_table = (attendances) => {
  /***
  Genera el HTML para la tabla de asistencia
  INPUT:
  assistances: array de objetos
  ESTRUCTURA 
  [{name:"Javier", asistencia: "SI"}...]
  asistencia: Si, No
  ***/
  let html = `<table><thead><tr><th>Nombres y apellidos</th><th>Presente</th><th>Ausente</th></tr></thead><tbody>`

  attendances.forEach((p) => {
    html += `<tr><td>${p.name || 'NA'}</td><td>${p.attendance ? 'X' : '&nbsp;'}</td><td>${!p.attendance ? 'X' : '&nbsp;'}</td></tr>`;
  });
  html += `</tbody></table>`
  return html       
}


const absences = $input.first().json.attendances
  .filter(persona => !persona.attendance)
  .map(persona => persona.name);

// Agregar el HTML generado al objeto principal
return {
  json: {
    ...items[0].json,
    attendeesHTML: attendeesHTML,
    topicsHTML:topics_meeting(),
    html_topics_covered: html_topics_covered,
    html_assistance_table: html_assistance_table($input.first().json.attendances),
    absences: absences
  }
};
