function armarSubResolucionesHTML(){
  // Preprocesar los temas de la reunión
  const subTemaObj = $input.first().json.tema_reunion.sub_topics
  let subTemaHTML = '';

  if (subTemaObj && subTemaObj.length > 0) {
    subTemaHTML += '<ul>';
    subTemaObj.forEach(subtema => {
      subTemaHTML += `<li>${subtema}</li>`;
    });
    subTemaHTML += '</ul>';
  }
  return subTemaHTML
}


function ordinalHasta100(numero) {
    if (numero < 1) return numero.toString();
    // Números básicos (1-30)
    const bases = {
        1: 'primera', 2: 'segunda', 3: 'tercera', 4: 'cuarta',
        5: 'quinta', 6: 'sexta', 7: 'séptima', 8: 'octava',
        9: 'novena', 10: 'décima', 11: 'undécima', 12: 'duodécima',
        13: 'decimotercera', 14: 'decimocuarta', 15: 'decimoquinta',
        16: 'decimosexta', 17: 'decimoséptima', 18: 'decimoctava',
        19: 'decimonovena', 20: 'vigésima', 30: 'trigésima',
        40: 'cuadragésima', 50: 'quincuagésima', 60: 'sexagésima',
        70: 'septuagésima', 80: 'octogésima', 90: 'nonagésima',
        100: 'centésima'
    };
    if (bases[numero]) return bases[numero];
    // Números compuestos (21-29, 31-39, etc.)
    const decena = Math.floor(numero / 10) * 10;
    const unidad = numero % 10;

    if (decena >= 20 && unidad > 0) {
        return `${bases[decena]} ${bases[unidad]}`;
    }
    return `posición ${numero}`;
}


function armarListaConsiderandos(){
  const consObj = $input.first().json.considerandos
  let considHTML = '';
  consObj.forEach(cons => {
    considHTML += `<p>${cons}</p>`;
  });
  return considHTML
}

const number = ordinalHasta100(parseInt($input.first().json.cod_sesion.match(/OCS-SO-(\d+)-2025/)?.[1] || 0));

// Agregar el HTML generado al objeto principal
return {
  json: {
    ...items[0].json,
    subtemasHTML: armarSubResolucionesHTML(),
    considerandosHTML: armarListaConsiderandos(),
    ordinal_number_session: number
  }
};
